    boolean isMirror(String s) {
        int len = s.length();
        for (int i = 0; i < len / 2; i++) {
            if (s.charAt(i) != s.charAt(len - 1 - i))
                return false;
        }
        return true;
    }

    public long kMirror(int k, int n) {
        int count = 0;
        long res = 0;
        for (long num : palindromes) {
            String s = convertToBase(Long.toString(num), k);
            if (isMirror(s)) {
                System.out.println(num + "\t" + s);
                res += num;
                count++;
            }
            if (count == n)
                return res;
        }
        return res;
    }

    String convertToBase(String num, int k) {
        return Integer.toString(
                Integer.parseInt(num, 10), k);
    }

    public void work() {
        System.out.println(kMirror(5, 15));
    }

    public static void main(String[] args) {
        new Main().work();
    }
